def get_transactions(address, api_key):
    url = f"https://api.etherscan.io/api?module=account&action=txlist&address={address}&sort=desc&page=1&offset=100&apikey={api_key}"

    response = requests.get(url)
    data = response.json()

    return data['result']

txns=get_transactions("0x3fC91A3afd70395Cd496C647d5a6CC9D4B2b7FAD","PGJ5AYE9WGD77YPS4F3NGQ33MB5YI7JYS8")
web3 = Web3(Web3.HTTPProvider('https://reth.shield3.com/rpc'))
uni=Uniswap()
for txn in txns:
    print(txn['hash'])
    val=uni.get_potential_slippage(web3,txn['input'])
    if val in ERROR_LIST:
        print(txn)
    else:
        print(val)
